import vBasePage from "../../BasePage/BasePage";
import ShellApi from "../webapi/ShellGlobal";
import config from "@/config/index";
var TableUniqueId = "appId";
var TerminalType = {
  "1": "总控",
  "2": "分控",
  "3": "HubApp",
  "4": "CCN"
};
export default {
  data: function data() {
    return {
      searchOptions: {
        searchOptions: {
          requiredCondition: false
        },
        seleFunc: {
          export: false,
          search: true
        },
        // exportUrl: ShellApi.GetAppInfoList,
        search_controls: [{
          type: "v_singleSelect",
          title: "部署点",
          key: "OrganiztionId",
          apiUrl: ShellApi.GetOrganiztionList,
          //params: { aaa: "fff" },
          selectkey: "organiztionId",
          selectvalue: "organiztionSN"
        }, {
          type: "v_singleSelect",
          title: "状态",
          key: "isDelete",
          data: [{
            typeid: 0,
            typename: "启用"
          }, {
            typeid: 1,
            typename: "禁用"
          }],
          selectkey: "typeid",
          selectvalue: "typename"
        }, {
          type: "v_singleSelect",
          title: "设备类型",
          key: "TerminalType",
          data: [{
            id: "1",
            value: "总控"
          }, {
            id: "2",
            value: "分控"
          }, {
            id: "3",
            value: "HubApp"
          }, {
            id: "4",
            value: "CCN"
          }],
          selectkey: "id",
          selectvalue: "value"
        }]
      },
      buttonOptions: [{
        title: "添加",
        key: "add",
        enable: true,
        iconStr: "fa-plus"
      }],
      editOptions: {
        dynamicEdits: {
          apiUrl: ShellApi.SubmitAppInfo,
          // params: {"test": "111"},
          list: [{
            lineType: "single",
            rowList: [{
              type: "v_text",
              title: "AppKey",
              key: "appKey",
              isRequest: true,
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "AppKey不能为空"
                },
                regexp: {
                  regexp: /^[A-Za-z0-9]+$/i,
                  message: "AppKey只能由字母字符和数字组成"
                },
                stringLength: {
                  max: 50,
                  message: "AppKey最多只能输入50字"
                }
              }
            }, {
              type: "v_text",
              title: "AppSecret",
              key: "appSecret",
              isRequest: true,
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "AppSecret不能为空"
                },
                regexp: {
                  regexp: /^[A-Za-z0-9]+$/i,
                  message: "AppKey只能由字母字符和数字组成"
                },
                stringLength: {
                  max: 50,
                  message: "AppSecret最多只能输入50字"
                }
              }
            }, {
              type: "v_singleSelect",
              title: "设备类型",
              key: "terminalType",
              data: [{
                terminalType: "1",
                value: "总控"
              }, {
                terminalType: "2",
                value: "分控"
              }, {
                terminalType: "3",
                value: "HubApp"
              }, {
                terminalType: "4",
                value: "CCN"
              }],
              selectkey: "terminalType",
              selectvalue: "value",
              isRequest: true,
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "设备类型不能为空"
                }
              }
            }, {
              type: "v_singleSelect",
              title: "部署点",
              key: "organiztionId",
              apiUrl: ShellApi.GetOrganiztionList,
              //params: { aaa: "fff" },
              selectkey: "organiztionId",
              selectvalue: "organiztionSN",
              isRequest: true,
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "部署点不能为空"
                }
              }
            }, {
              type: "v_text",
              title: "终端识别项",
              key: "identifyingNumber",
              isRequest: true,
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "终端识别项不能为空"
                }
              },
              stringLength: {
                max: 50,
                message: "终端识别项最多只能输入50字"
              }
            }, {
              type: "v_textarea",
              title: "备注",
              key: "remark",
              isRequest: false,
              validators: {
                notEmpty: {
                  enabled: false
                }
              }
            }]
          }]
        }
      },
      tableOption: {
        tableId: "proofreport",
        tableName: "凭据统计报表",
        url: config.pgyerApiUrl + ShellApi.GetAppInfoList.options.url,
        method: ShellApi.GetAppInfoList.options.method,
        uniqueId: TableUniqueId,
        actions: [{
          key: "tableEdit",
          title: "编辑",
          url: ShellApi.GetAppInfoById,
          method: ShellApi.GetAppInfoById.method,
          // params: {
          //   aaa: "fff"
          // },
          tabIdx: 2
        }, {
          key: "tablePause",
          statuKey: "isDelete",
          // statuObj: {
          //   'false': "启用",
          //   'true': "禁用"
          // },
          title: "启用/禁用",
          tabIdx: 0
        }, {
          key: "tableDownload",
          url: ShellApi.GetAppXMLDownLoadUrl,
          method: ShellApi.GetAppXMLDownLoadUrl.method,
          title: "下载",
          tabIdx: 1
        }],
        columns: [{
          field: "organiztionSN",
          title: "部署点编号",
          align: "center"
        }, {
          field: "appKey",
          title: "AppKey",
          align: "center"
        }, {
          field: "appSecret",
          title: "AppSecret",
          align: "center"
        }, {
          field: "terminalType",
          title: "设备类型",
          align: "center",
          formatter: function formatter(val, row) {
            return TerminalType[row.terminalType];
          }
        }, {
          field: "isDelete",
          title: "状态",
          align: "center",
          formatter: function formatter(val, row) {
            return row.isDelete ? "启用" : "禁用";
          }
        }]
      }
    };
  },
  methods: {
    rowClick: function rowClick(rowItem, element, baseObject) {
      console.log(element);

      var _ = this;

      if (element.id == "tablePause") {
        ShellApi.UpdateDeleteStatus;

        var currentAction = _.tableOption.actions.find(function (item) {
          return item.key == element.id;
        });

        var param = {};
        param["isDelete"] = rowItem[currentAction.statuKey] ? 0 : 1;
        param[_.tableOption.uniqueId] = rowItem[_.tableOption.uniqueId];
        ShellApi.UpdateDeleteStatus(param).then(function (res) {
          baseObject.$refs.table.operation("refresh");
        });
      }
    },
    componentMountBefore: function componentMountBefore(element, basepage) {
      debugger;
    },
    componentMountAfter: function componentMountAfter(element, basepage) {}
  },
  components: {
    vBasePage: vBasePage
  }
};
