import vBasePage from "../../BasePage/BasePage";
import NewApi from "../webapi/New";
import config from "@/config/index";
var TableUniqueId = "appId";
var TerminalType = {
  "1": "总控",
  "2": "分控",
  "3": "HubApp",
  "4": "CCN"
};
export default {
  data: function data() {
    return {
      searchOptions: {
        searchOptions: {
          requiredCondition: false
        },
        seleFunc: {
          export: false,
          search: true
        },
        // exportUrl: NewApi.GetAppInfoList,
        search_controls: [{
          type: "v_singleSelect",
          title: "部署点",
          key: "OrganiztionId",
          apiUrl: NewApi.GetOrganiztionList,
          //params: { aaa: "fff" },
          selectkey: "organiztionId",
          selectvalue: "organiztionSN"
        }, {
          type: "v_singleSelect",
          title: "状态",
          key: "isDelete",
          data: [{
            typeid: 0,
            typename: "启用"
          }, {
            typeid: 1,
            typename: "禁用"
          }],
          selectkey: "typeid",
          selectvalue: "typename"
        }, {
          type: "v_singleSelect",
          title: "设备类型",
          key: "TerminalType",
          data: [{
            id: "1",
            value: "总控"
          }, {
            id: "2",
            value: "分控"
          }, {
            id: "3",
            value: "HubApp"
          }, {
            id: "4",
            value: "CCN"
          }],
          selectkey: "id",
          selectvalue: "value"
        }]
      },
      buttonOptions: [{
        title: "添加",
        key: "add",
        enable: true,
        iconStr: "fa-plus"
      }],
      editOptions: {
        dynamicEdits: {
          apiUrl: NewApi.SubmitAppInfo,
          // params: {"test": "111"},
          list: [{
            lineType: "single",
            rowList: [{
              type: "v_singleSelect",
              title: "部署点",
              key: "organiztionId",
              apiUrl: NewApi.GetOrganiztionList,
              hasValChange: true,
              //params: { aaa: "fff" },
              selectkey: "organiztionId",
              selectvalue: "organiztionSN"
            }, {
              type: "v_text",
              title: "AppSecret",
              key: "appSecret",
              isRequest: true,
              editDisabled: true,
              //编辑环境下是否禁用
              addDisabled: true,
              //添加环境下是否禁用
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "AppSecret不能为空"
                },
                regexp: {
                  regexp: /^[A-Za-z0-9]+$/i,
                  message: "AppKey只能由字母字符和数字组成"
                },
                stringLength: {
                  max: 50,
                  message: "AppSecret最多只能输入50字"
                }
              }
            }, {
              type: "v_upload",
              key: "uploadFile",
              title: "请选择上传图片",
              isRequest: true,
              uploadNum: 3,
              // 文件数量,默认为1，多张图片以，号拼接字符串形式提交
              fileSizeLimit: 3,
              // 文件大小  以兆为单位
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "选择上传的文件不能为空"
                }
              }
            }, {
              type: "v_upload",
              key: "uploadFile1",
              title: "请选择上传图片",
              isRequest: true,
              uploadNum: 3,
              // 文件数量,默认为1，多张图片以，号拼接字符串形式提交
              fileSizeLimit: 3,
              // 文件大小  以兆为单位
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "选择上传的文件不能为空"
                }
              }
            }, {
              type: "v_upload",
              key: "uploadFile2",
              title: "请选择上传图片",
              isRequest: true,
              uploadNum: 3,
              // 文件数量,默认为1，多张图片以，号拼接字符串形式提交
              fileSizeLimit: 3,
              // 文件大小  以兆为单位
              validators: {
                notEmpty: {
                  enabled: true,
                  message: "选择上传的文件不能为空"
                }
              }
            }]
          }]
        }
      },
      tableOption: {
        tableId: "proofreport",
        tableName: "凭据统计报表",
        url: config.pgyerApiUrl + NewApi.GetAppInfoList.options.url,
        method: NewApi.GetAppInfoList.options.method,
        uniqueId: TableUniqueId,
        actions: [{
          key: "tableEdit",
          title: "编辑",
          url: NewApi.GetAppInfoById,
          method: NewApi.GetAppInfoById.method,
          // params: {
          //   aaa: "fff"
          // },
          tabIdx: 2
        }, {
          key: "tablePause",
          statuKey: "isDelete",
          // statuObj: {
          //   'false': "启用",
          //   'true': "禁用"
          // },
          title: "启用/禁用",
          tabIdx: 0
        }, {
          key: "tableDownload",
          url: NewApi.GetAppXMLDownLoadUrl,
          method: NewApi.GetAppXMLDownLoadUrl.method,
          title: "下载",
          tabIdx: 1
        }],
        columns: [{
          field: "organiztionSN",
          title: "产品编码",
          align: "center"
        }, {
          field: "appKey",
          title: "产品名称",
          align: "center"
        }, {
          field: "appSecret",
          title: "生产批号",
          align: "center"
        }, {
          field: "terminalType",
          title: "上传日期",
          align: "center",
          formatter: function formatter(val, row) {
            return TerminalType[row.terminalType];
          }
        }]
      }
    };
  },
  methods: {
    rowClick: function rowClick(rowItem, element, baseObject) {
      console.log(element);

      var _ = this;

      if (element.id == "tablePause") {
        NewApi.UpdateDeleteStatus;

        var currentAction = _.tableOption.actions.find(function (item) {
          return item.key == element.id;
        });

        var param = {};
        param["isDelete"] = rowItem[currentAction.statuKey] ? 0 : 1;
        param[_.tableOption.uniqueId] = rowItem[_.tableOption.uniqueId];
        NewApi.UpdateDeleteStatus(param).then(function (res) {
          baseObject.$refs.table.operation("refresh");
        });
      }
    },
    componentMountBefore: function componentMountBefore(element, basepage) {
      debugger;
    },
    componentMountAfter: function componentMountAfter(element, basepage) {},
    // 编辑模态框中  组件值发生变化
    componentValueChange: function componentValueChange(element, basepage) {
      var editChildArr = basepage.$refs.editModule.$refs.child || [];

      if (element.options.key == "organiztionId") {
        var target = editChildArr.find(function (item) {
          return item.options.key == "appSecret";
        });
        target.setValue(element.val); // debugger;
      }
    }
  },
  components: {
    vBasePage: vBasePage
  }
};
