  mounted: function mounted() {},
  methods: {
    // 表格回调事件名规则   传入的按钮 key + Block
    // 编辑按钮回调事件
    tableEditBlock: function tableEditBlock(rowId) {
      var obj = this.$refs.table.tableObj.bootstrapTable("getRowByUniqueId", rowId);
      this.getSeleReportDataDetail(obj); // console.log(obj);
    },
    // 禁用按钮回调事件
    tablePauseBlock: function tablePauseBlock(rowId) {
      var obj = this.$refs.table.tableObj.bootstrapTable("getRowByUniqueId", rowId);
      this.deleReportData(rowId);
    },
    // 动态搜索回调
    search: function search(params) {
      this.refershTable(params);
    },
    // 新建 & 编辑表单记录完成后的回调事件
    editCallback: function editCallback(type, params) {
      // type = add   type = edit
      this.refershTable(params);
    },
    getSeleReportDataDetail: function getSeleReportDataDetail(reportObj) {
      var _ = this;

      TableApi.OutWareHoustDetailReport({
        reportId: reportObj["receipT_NO"]
      }).then(function (res) {
        console.log(res);
        _.seleTableObj = res.result;

        _.showAddRecordModal();
      });
    },
    deleReportData: function deleReportData(reportId) {
      var _ = this;

      TableApi.DeleteTableRecord({
        reportId: reportId
      }).then(function (res) {
        console.log(res);

        if (res.success) {
          _.$refs.table.tableObj.bootstrapTable("removeByUniqueId", reportId);

          layer.msg("删除成功");
        }
      });
    },
    // 刷新表单
    refershTable: function refershTable(params) {
      this.$refs.table.tableOption.url = config.pgyerApiUrl + TableApi.TableDataList.options.url;
      this.$refs.table.tableOption.params = params;
      this.$refs.table.operation("refresh");
    },
    // 显示新建 & 编辑表单记录弹框
    showAddRecordModal: function showAddRecordModal() {
      var _ = this;

      _.isEdit = true;

      _.$nextTick(function () {
        _.layerId = layer.open({
          type: 1,
          title: "添加报告记录",
          maxmin: false,
          area: ["70%", "70%"],
          content: $("#pop"),
          end: function end() {
            _.isEdit = false;
            _.seleTableObj = {};
          }
        });
      });
    }
  },
  components: {
