import "bootstrap-select/dist/css/bootstrap-select.min.css";
import "bootstrap-select";
export default {
  data: function data() {
    return {
      val: "",
      parentId: "",
      List: [],
      response: []
    };
  },
  watch: {
    val: function val(_val) {
      if (this.response.length) {
        for (var i = 0; i < this.response.length; i++) {
          var responseItem = this.response[i];

          if (responseItem[this.options.selectkey] == _val) {
            this.options.item = responseItem;
          }
        }
      }

      this.options.hasValChange && this.$emit("componentValueChange", this);
      this.$emit("callback", this);
    },
    parentId: function parentId() {
      this.init();
    }
  },
  props: ["options"],
  beforeMount: function beforeMount() {
    this.options.hasMountBefore && this.$emit("componentMountBefore", this);
  },
  //apiUrl params selectkey selectvalue
  mounted: function mounted() {
    this.init();
  },
  methods: {
    init: function init() {
      var _ = this;

      $(_.$refs.select).selectpicker({
        noneSelectedText: "",
        noneResultsText: "",
        liveSearch: true,
        size: 5 //设置select高度，同时显示5个值

      });
      _.List = [];

      _.List.push({
        id: "",
        value: _.options.title
      });

      if (_.options.data) {
        _.refreshData(_.options.data);
      } else {
        if (_.options.parendPostKey) {
          var postObj = {};
          postObj[_.options.parendPostKey] = _.parentId;
          Object.assign(_.options.params ? _.options.params : _.options.params = {}, postObj);
        } // debugger;


        _.options.apiUrl(_.options.params).then(function (res) {
          if (res.success) {
            _.response = res.result;

            _.refreshData(res.result);
          }
        });
      }
    },
    setValue: function setValue(val) {
      var _ = this;

      _.val = val;
      $(_.$refs.select).selectpicker("val", val);
      $(_.$refs.select).selectpicker("refresh");
    },
    getValue: function getValue() {
      return $(this.$refs.select).val();
    },
    refreshData: function refreshData(list) {
      var _this = this;

      var _ = this;

      list.forEach(function (item) {
        _.List.push({
          id: item[_.options.selectkey],
          value: item[_.options.selectvalue]
        });
      });
      setTimeout(function () {
        $(_.$refs.select).selectpicker("refresh"); // _.$emit("loadCallBack", this);

        _.$emit("componentMountAfter", _);

        if (_this.options.isFromEdit && _this.options.editDisabled) {
          $(_.$refs.select).attr("disabled", true);
          $(_.$refs.select).selectpicker("refresh");
        }

        if (!_this.options.isFromEdit && _this.options.addDisabled) {
          $(_.$refs.select).attr("disabled", true);
          $(_.$refs.select).selectpicker("refresh");
        }
      }, 100);
    }
  }
};
