export default {
  data() {
    return {
      searchOptions: {
        searchOptions: {
          requiredCondition: false
        },
        seleFunc: {
          export: false,
          search: true
        },
        // exportUrl: ShellApi.GetAppInfoList,
        search_controls: [
          {
            type: "v_singleSelect",
            title: "部署点",
            key: "OrganiztionId",
            apiUrl: ShellApi.GetOrganiztionList,
            //params: { aaa: "fff" },
            selectkey: "organiztionId",
            selectvalue: "organiztionSN"
          },
          {
            type: "v_singleSelect",
            title: "状态",
            key: "isDelete",
            data: [
              {
                typeid: 0,
                typename: "启用"
              },
              {
                typeid: 1,
                typename: "禁用"
              }
            ],
            selectkey: "typeid",
            selectvalue: "typename"
          },
          {
            type: "v_singleSelect",
            title: "设备类型",
            key: "TerminalType",
            data: [
              {
                id: "1",
                value: "总控"
              },
              {
                id: "2",
                value: "分控"
              },
              {
                id: "3",
                value: "HubApp"
              },
              {
                id: "4",
                value: "CCN"
              }
            ],
            selectkey: "id",
            selectvalue: "value"
          }
        ]
      },
      buttonOptions: [
        {
          title: "添加",
          key: "add",
          enable: true,
          iconStr: "fa-plus"
        }
      ],
      editOptions: {
        dynamicEdits: {
          apiUrl: ShellApi.SubmitAppInfo,
          // params: {"test": "111"},
          list: [
            {
              lineType: "single",
              rowList: [
                {
                  type: "v_text",
                  title: "AppKey",
                  key: "appKey",
                  isRequest: true,
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "AppKey不能为空"
                    }
                  }
                },
                {
                  type: "v_text",
                  title: "AppSecret",
                  key: "appSecret",
                  isRequest: true,
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "AppSecret不能为空"
                    }
                  }
                },
                {
                  type: "v_singleSelect",
                  title: "设备类型",
                  key: "terminalType",
                  data: [
                    {
                      terminalType: "1",
                      value: "总控"
                    },
                    {
                      terminalType: "2",
                      value: "分控"
                    },
                    {
                      terminalType: "3",
                      value: "HubApp"
                    },
                    {
                      terminalType: "4",
                      value: "CCN"
                    }
                  ],
                  selectkey: "terminalType",
                  selectvalue: "value",
                  isRequest: true,
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "设备类型不能为空"
                    }
                  }
                },
                {
                  type: "v_singleSelect",
                  title: "部署点",
                  key: "organiztionId",
                  apiUrl: ShellApi.GetOrganiztionList,
                  //params: { aaa: "fff" },
                  selectkey: "organiztionId",
                  selectvalue: "organiztionSN",
                  isRequest: true,
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "部署点不能为空"
                    }
                  }
                },
                {
                  type: "v_text",
                  title: "终端识别项",
                  key: "identifyingNumber",
                  isRequest: true,
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "AppKey不能为空"
