import vTable from "@c/tables/table";
import vSearch from "@c/dynamicSearch/dynamic_Search";
import api from "../webapi/user";
import moment from "moment";
import upload from "@c/upload";
import config from "@/config/index";
export default {
  data() {
    var _ = this;
    return {
      startTime: "",
      endTime: "",
      uploadUrl: api.upload,
      uploadParams: {
        aa: "123"
      },
      isEdit: true,
      formData: {
        No: "", //编号
        Name: "", //名称
        Code: "", //编码
        MnenonicCode: "" //助记码
      },
      search_controls: [
        {
          type: "v_text",
          title: "测试文本",
          key: "txt1"
        },
        {
          type: "v_text",
          title: "测试文本1",
          key: "txt2"
        },
        {
          type: "v_datetime",
          title: "开始时间",
          key: "startTime"
        },
        {
          type: "v_datetime",
          title: "结束时间",
          key: "endTime"
        },
        // {
        //   type: "v_singleSelect",
        //   title: "选择项",
        //   key: "aaabbb",
        //   apiUrl: api.get_UserListSelect,
        //   //params: { aaa: "fff" },
        //   selectkey: "id",
        //   selectvalue: "title"
        // },
        {
          type: "v_doubleSelect",
          key: "aaabbbbdddd",
          list: [
            {
              type: "v_singleSelect",
              title: "省选择",
              key: "sheng",
              apiUrl: api.get_UserListSelect,
              selectkey: "id",
              selectvalue: "title",
              childrenkey: "shi"
            },
            {
              type: "v_singleSelect",
              title: "市选择",
              key: "shi",
              apiUrl: api.get_UserListSelect,
              parendPostKey: "shengId",
              childrenkey: "qu",
              selectkey: "id",
              selectvalue: "title"
            },
            {
              type: "v_singleSelect",
              title: "区选择",
              key: "qu",
              apiUrl: api.get_UserListSelect,
              parendPostKey: "shiId",
              selectkey: "id",
              selectvalue: "title"
            }
          ]
        }
      ],
      tableOption: {
        url: config.pgyerApiUrl + api.get_UserList.options.url,
        method: api.get_UserList.options.method,
        columns: [
          {
            field: "title",
            title: "标题",
            align: "center"
          },
          { field: "text", title: "内容", align: "center" },
          {
            field: "creationTime",
            title: "创建时间",
            align: "center",
            formatter: function(val, row) {
              return moment(val).format("YYYY-MM-DD ");
            }
          },
          {
            field: "test",
            title: "操作",
            align: "center",
            sortable: true,
            formatter: function(val, row) {
              return (
                '<div clss="btn-group">' +
                '<a class="btn btn-info btn-sm"  iid="' +
                val +
                '">详情</a>' +
                "</div>"
              );
            }
          }
        ]
      }
    };
  },
  watch: {
    isEdit: {
      handler: function(curVal, oldVal) {
        if (curVal) {
          var _ = this;
          setTimeout(function() {
            _.initValidate();
          }, 100);
        }
      }
    }
  },
  mounted: function() {
    // api.ppp({}).then(res => {
    //   console.log(res);
    // });
    var _ = this;
    _.$refs.table.tableObj.on("click", "a", function() {
      _.editModel();
    });
  },
  methods: {
    uploadCallback: function(a, b) {
      console.log(a);
      console.log(b);
    },
    search: function(params) {
      this.$refs.table.tableOption.params = params;
      this.$refs.table.operation("refresh");
    },
    initValidate: function() {
      this.validator = $(this.$refs.formBody).bootstrapValidator({
        fields: {
          No: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "编号只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "编号不可为空！"
              }
            }
          },
          Name: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "名称只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "名称不可为空！"
              }
            }
          }
        }
      });
    },
    editModel: function() {
      var _ = this;
      _.isEdit = true;
      setTimeout(function() {
        _.layerId = layer.open({
          type: 1,
          title: "测试单据详情",
          maxmin: false,
          area: ["70%", "70%"],
          content: $("#edit"),
          end: function() {
            _.isEdit = false;
          }
        });
      }, 50);
    },
    save: function() {
      var validate = $(this.$refs.formBody).data("bootstrapValidator");
      validate.validate();
      if (validate.isValid()) {
        console.log(this.formData);
        layer.close(this.layerId);
      }
    }
  },
  components: { vTable, vSearch, upload }
};
