import vTable from "@c/tables/table";
import vSearch from "@c/dynamicSearch/dynamic_Search";
import vEditTable from "@c/edit_table";

import AreaApi from "../../DictModule/webapi/AreaApi";
import DataApi from "../../DictModule/webapi/DataApi";
import MatlApi from "../../MatlModule/webapi/MaterialsApi";
import ReportApi from "../webapi/ReportApi";
import moment from "moment";
import config from "@/config/index";
import { pageTool } from "@/js/util.js";
export default {
  data() {
    var _ = this;
    return {
      isEdit: false,
      formData: {
        No: "", //编号
        Name: "", //名称
        Code: "", //编码
        MnenonicCode: "" //助记码
      },
      // 选择所需的功能按钮
      // seleFunc: { add: true, export: true, search: true },
      // 动态创建所需的功能按钮，添加 和 导出 功能已封装完善，默认显示
      seleFunc: {
        search: true,
        export: true,
        addFunList: [
          {
            title: "添加",
            iconStr: "fa-plus",
            method: function() {
              _.showAddRecordModal();
            }
          },
          {
            title: "下载",
            method: function(e, item) {
              console.log("下载被点击");
            }
          }
        ]
      },
      buttonOptions: [
        // {
        //   title: "下载",
        //   method: function(e, item) {
        //     console.log("下载被点击");
        //     debugger;
        //     let seleCheckBoxs = _.$refs.table.tableObj.bootstrapTable(
        //       "getSelections"
        //     );
        //   }
        // },
        {
          title: "添加",
          iconStr: "fa-plus",
          method: function() {
            _.showAddRecordModal();
          }
        }
      ],
      search_controls: [
        {
          type: "v_text",
          title: "发货单号1",
          key: "receiptno"
        },
        {
          type: "v_singleSelect",
          title: "选择产品",
          key: "materialsid",
          apiUrl: MatlApi.GetMaterialsList,
          //params: { aaa: "fff" },
          selectkey: "productId",
          selectvalue: "productName"
        },
        {
          type: "v_singleSelect",
          title: "选择产品固定值",
          key: "materials",
          data: [
            {
              id: "",
              value: "请选择我"
            },
            {
              id: "640f563421d14d14acc125ef5499e99c",
              value: "dsfdsfds"
            },
            {
              id: "6557a08e381b450ebd9398de8166e665",
              value: "测试物料"
            }
          ],
          //params: { aaa: "fff" },
          selectkey: "id",
          selectvalue: "value"
        },
        {
          type: "v_text",
          title: "批次号",
          key: "batchno"
        },
        {
          type: "v_datetime",
          title: "发货开始日期",
          key: "sendstartdate"
          //defdate: moment(new Date()).add(-1, 'months').format("YYYY-MM-DD")
        },
        {
          type: "v_datetime",
          title: "发货结束日期",
          key: "sendenddate"
        },
        {
          type: "br",
          key: "bra"
        },
        {
          type: "v_doubleSelect",
          key: "sendprovincecity",
          list: [
            {
              type: "v_singleSelect",
              title: "发货省份",
              key: "sendprovince",
              apiUrl: AreaApi.GetAllProvinceList,
              selectkey: "provinceid",
              selectvalue: "provincename",
              childrenkey: "sendcity"
            },
            {
              type: "v_singleSelect",
              title: "发货城市",
              key: "sendcity",
              apiUrl: AreaApi.GetCityOfProvinceList,
              parendPostKey: "AreaId",
              selectkey: "cityid",
              selectvalue: "cityname"
            }
          ]
        },
        {
          type: "v_doubleSelect",
          key: "sendlevelunit",
          list: [
            {
              type: "v_singleSelect",
              title: "发货层级",
              key: "sendlevel",
              apiUrl: DataApi.GetSendUnitLevelList,
              selectkey: "dataid",
              selectvalue: "dataname",
              childrenkey: "sendunit"
            },
            {
              type: "v_singleSelect",
              title: "发货单位",
              key: "sendunit",
              apiUrl: DataApi.GetSendOutWareHoustList,
              parendPostKey: "CatagoryId",
              selectkey: "id",
              selectvalue: "name"
            }
          ]
        }
      ],
      // vEditTable 组件数据
      dynamicEdits: {
        // 保存数据API
        apiUrl: ReportApi.AddTableRecord,
        paras: {},
        list: [
          {
            // 每行显示编辑框数量类型   可选值  single、duoble
            lineType: "double",
            rowList: [
              {
                type: "v_text",
                title: "产品名称",
                key: "materialS_NAME", // 需对应后台返回字段名称
                // 是否为必填选项
                isRequest: true,
                // 校验规则
                validators: {
                  notEmpty: {
                    enabled: true,
                    message: "产品名称不能为空"
                  }
                }
              },
              {
                type: "v_text",
                title: "产品编码",
                key: "materialS_SN",
                isRequest: false,
                validators: {
                  notEmpty: {
                    enabled: false
                  }
                }
              },
               {
                type: "v_text",
                title: "产品名称11",
                key: "productName",
                isRequest: true,
                validators: {
                  notEmpty: {
                    enabled: true,
                    message: "产品名称不能为空"
                  }
                }
              },
              {
                type: "v_text",
                title: "产品编码11",
                key: "deviceno",
                isRequest: false,
                validators: {
                  notEmpty: {
                    enabled: false
                  }
                }
              },
              {
                type: "v_text",
                title: "发货单号",
                key: "receipT_NO",
                isRequest: true,
                validators: {
                  notEmpty: {
                    enabled: true,
                    message: "发货单号不能为空"
                  }
                }
              },
              {
                type: "v_text",
                title: "批次号",
                key: "batchNo",
                isRequest: false,
                validators: {
                  notEmpty: {
                    enabled: false
                  }
                }
              },
                            {
                type: "v_singleSelect",
                title: "选择产品",
                key: "materialsid",
                apiUrl: MatlApi.GetMaterialsList,
                //params: { aaa: "fff" },
                selectkey: "productId",
                selectvalue: "productName",
                isRequest: true,
                validators: {
                  notEmpty: {
                    enabled: true
                  }
                }
              },
              {
                lineType: "single",
                type: "v_datetime",
                title: "发货日期",
                key: "receipT_DATE",
                isRequest: true,
                validators: {
                  notEmpty: {
                    enabled: true
                  }
                }
                //defdate: moment(new Date()).add(-1, 'months').format("YYYY-MM-DD")
              }
            ]
          },
          {
            lineType: "single",
            rowList: [
              {
                type: "v_doubleSelect",
                key: "sendprovincecity",
                isRequest: true,
                list: [
                  {
                    type: "v_singleSelect",
                    title: "发货省份",
                    key: "sendprovince",
                    apiUrl: AreaApi.GetAllProvinceList,
                    selectkey: "provinceid",
                    selectvalue: "provincename",
                    childrenkey: "sendcity",
                    isRequest: false,
                    validators: {
                      notEmpty: {
                        enabled: true
                      }
                    }
                  },
                  {
                    type: "v_singleSelect",
                    title: "发货城市",
                    key: "sendcity",
                    apiUrl: AreaApi.GetCityOfProvinceList,
                    parendPostKey: "AreaId",
                    selectkey: "cityid",
                    selectvalue: "cityname",
                    isRequest: true,
                    validators: {
                      notEmpty: {
                        enabled: true
                      }
                    }
                  }
                ]
              }
            ]
          }
        ]
      },
      seleTableObj: {}, // 选中的表单行数据

      searchOptions: {
        requiredCondition: false
      },
      tableOption: {
        tableId: "sendreport",
        tableName: "发货统计报表",
        url: config.pgyerApiUrl + ReportApi.OutWareHoustReportPage.options.url,
        method: ReportApi.OutWareHoustReportPage.options.method,
        uniqueId: "receipT_NO",
        // 自定义操作按钮
        actions: [
          {
            key: "tablePause",
            title: "禁用",
            buttonType: "btn-danger"
          },
          {
            key: "tableEdit", // 回调方法名
            title: "编辑"
          }
        ],
        columns: [
          {
            field: "materialS_NAME",
            title: "产品名称",
            align: "left",
            width: "150px",
            cellStyle: function(value, row, index) {
              return {
                css: {
                  overflow: "hidden",
                  "text-overflow": "ellipsis",
                  "white-space": "nowrap",
                  display: "inline-block"
                }
              };
            }
          },
          { field: "receipT_NO", title: "发货单号", align: "center" },
          { field: "sendProvince", title: "发货省", align: "center" },
          { field: "sendCity", title: "发货城市", align: "center" },
          { field: "receipT_SENDER", title: "发货单位", align: "center" },
          {
            field: "receipT_DATE",
            title: "发货日期",
            align: "center",
            formatter: function(val, row) {
              return moment(val).format("YYYY-MM-DD HH:mm:ss");
            }
          }
        ]
      }
    };
  },
  computed: {
    exportOptions: function() {
      return {
        id: this.tableOption.tableId,
        name: this.tableOption.tableName,
        url: ReportApi.OutWareHoustReportExport
      };
    }
  },
  mounted: function() {},
  methods: {
    tableEditBlock: function(rowItem) {
      let _ = this;
      ReportApi.OutWareHoustDetailReport({
        reportId: rowItem["receipT_NO"]
      }).then(res => {
        debugger;
        _.seleTableObj = res.result;
        _.showAddRecordModal();
      });
    },
    // 禁用按钮回调事件
    tablePauseBlock: function(rowItem) {
      let _ = this;
      var reportId = rowItem["receipT_NO"];
      // 删除单条报表数据
      ReportApi.DeleteTableRecord({
        reportId: reportId
      }).then(res => {
        console.log(res);
        if (res.success) {
          _.$refs.table.tableObj.bootstrapTable("removeByUniqueId", reportId);
          layer.msg("删除成功");
        }
      });
    },
    // 动态搜索回调
    search: function(params) {
      this.refershTable(params);
    },
    // 新建 & 编辑表单记录完成后的回调事件
    editCallback: function(type, params) {
      // type = add   type = edit
      this.refershTable(params);
    },
    // 刷新表单
    refershTable: function(params) {
      this.$refs.table.tableOption.url =
        config.pgyerApiUrl + ReportApi.OutWareHoustReportPage.options.url;
      this.$refs.table.tableOption.params = params;
      this.$refs.table.operation("refresh");
    },
    // 显示新建 & 编辑表单记录弹框
    showAddRecordModal: function() {
      let _ = this;
      _.isEdit = true;
      _.$nextTick(() => {
        _.layerId = layer.open({
          type: 1,
          title: "添加报告记录",
          maxmin: false,
          area: ["70%", "70%"],
          content: $("#pop"),
          end: function() {
            _.isEdit = false;
            _.seleTableObj = {};
          }
        });
      });
    }
  },

  components: { vTable, vSearch, vEditTable }
};
