            key: "OrganiztionId",
            apiUrl: NewApi.GetOrganiztionList,
            //params: { aaa: "fff" },
            selectkey: "organiztionId",
            selectvalue: "organiztionSN"
          },
          {
            type: "v_singleSelect",
            title: "状态",
            key: "isDelete",
            data: [
              {
                typeid: 0,
                typename: "启用"
              },
              {
                typeid: 1,
                typename: "禁用"
              }
            ],
            selectkey: "typeid",
            selectvalue: "typename"
          },
          {
            type: "v_singleSelect",
            title: "设备类型",
            key: "TerminalType",
            data: [
              {
                id: "1",
