          iconStr: "fa-plus"
        }
      ],
      editOptions: {
        dynamicEdits: {
          apiUrl: NewApi.SubmitAppInfo,
          // params: {"test": "111"},
          list: [
            {
              lineType: "single",
              rowList: [
                {
                  type: "v_singleSelect",
                  title: "部署点",
                  key: "organiztionId",
                  apiUrl: NewApi.GetOrganiztionList,
                  hasValChange: true,
                  //params: { aaa: "fff" },
                  selectkey: "organiztionId",
                  selectvalue: "organiztionSN"
                },
                {
                  type: "v_text",
                  title: "AppSecret",
                  key: "appSecret",
                  isRequest: true,
                  editDisabled: true, //编辑环境下是否禁用
                  addDisabled: true, //添加环境下是否禁用
                  validators: {
                    notEmpty: {
                      enabled: true,
                      message: "AppSecret不能为空"
                    },
                    regexp: {
                      regexp: /^[A-Za-z0-9]+$/i,
                      message: "AppKey只能由字母字符和数字组成"
                    },
                    stringLength: {
                      max: 50,
                      message: "AppSecret最多只能输入50字"
                    }
                  }
                },
                {
                  type: "v_upload",
                  key: "uploadFile",
                  title: "请选择上传图片",
                  isRequest: true,
                  uploadNum: 3, // 文件数量,默认为1，多张图片以，号拼接字符串形式提交
                  fileSizeLimit: 3, // 文件大小  以兆为单位
