import vTable from "@c/tables/table";
import AreaApi from "../webapi/AreaApi";
import moment from "moment";
import config from "@/config/index";
export default {
  data() {
    var _ = this;
    return {
      isEdit: false,
      formData: {
        No: "", //编号
        Name: "", //名称
        Code: "", //编码
        MnenonicCode: "" //助记码
      },
      tableOption: {
        url: config.pgyerApiUrl + AreaApi.GetAllProvinceList.options.url,
        method: AreaApi.GetAllProvinceList.options.method,
        columns: [
          { field: "provinceid", title: "省编号", align: "center" },
          { field: "provincename", title: "省名称", align: "center" },
          {
            field: "test",
            title: "操作",
            align: "center",
            sortable: true,
            formatter: function(val, row) {
              return (
                '<div clss="btn-group">' +
                '<a class="btn btn-info btn-sm"  iid="' +
                val +
                '">详情</a>' +
                "</div>"
              );
            }
          }
        ]
      },
      detailtableOption: {
        url: config.pgyerApiUrl + AreaApi.GetCityOfProvinceList.options.url,
        method: AreaApi.GetCityOfProvinceList.options.method,
        columns: [
          { field: "cityid", title: "城市编号", align: "center" },
          { field: "cityname", title: "城市名称", align: "center" }
        ]
      }
    };
  },
  watch: {
    isEdit: {
      handler: function(curVal, oldVal) {
        if (curVal) {
          var _ = this;
          setTimeout(function() {
            _.initValidate();
          }, 100);
        }
      }
    }
  },
  mounted: function() {
    var _ = this;
    _.$refs.table.tableObj.on("click", "a", function() {
      _.editModel();
    });
  },
  methods: {
    search: function(params) {
      this.$refs.table.tableOption.params = params;
      this.$refs.table.operation("refresh");
    },
    initValidate: function() {
      this.validator = $(this.$refs.formBody).bootstrapValidator({
        fields: {
          No: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "编号只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "编号不可为空！"
              }
            }
          },
          Name: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "名称只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "名称不可为空！"
              }
            }
          }
        }
      });
    },
    editModel: function() {
      var _ = this;
      _.isEdit = true;
      setTimeout(function() {
        _.layerId = layer.open({
          type: 1,
          title: "城市详情",
          maxmin: false,
          area: ["70%", "70%"],
          content: $("#edit"),
          end: function() {
            _.isEdit = false;
          }
        });
      }, 50);
    }
  },
  components: { vTable }
};
