          key: "add",
          enable: true,
          iconStr: "fa-plus"
        }
      ],
      editOptions: {
        dynamicEdits: {
          apiUrl: NewApi.SaveCheckReport,
          list: [
            {
              lineType: "single",
              rowList: [
                {
                  type: "v_singleSelect",
                  title: "生产批次",
                  key: "batchNo",
                  isRequest: true,
                  apiUrl: NewApi.GetBatchList,
                  hasValChange: true,
                  editDisabled: true,
