import vBasePage from "../../BasePage/BasePage";
import FileManagerApi from "../webapi/FileManager";
import moment from "moment";
import config from "@/config/index";

var TableUniqueId = "FileId";
var EquipmentType = {
  "1": "采集器枪号",
  "2": "Hubapp手机设备号",
  "3": "服务器设备号"
};
var FileSource = {
  "1": "生产文件",
  "2": "物流文件"
};
var Platform = {
  "1": "DTS",
  "2": "OEM"
};
export default {
  data() {
    var _ = this;
    return {
      searchOptions: {
        searchOptions: {
          requiredCondition: false
        },
        seleFunc: {
          export: false,
          search: true
        },
        search_controls: [
          {
            type: "v_text",
            title: "上传点编号",
            key: "PlaceId"
          },
          {
            type: "v_text",
            title: "枪号",
            key: "EquipmentNumber"
          },
          {
            type: "v_singleSelect",
            title: "文件类型",
            key: "FileSource",
            data: [
              {
                id: '1',
                value: '生产文件',
              },
              {
                id: '2',
                value: '物流文件',
              }
            ],
            selectkey: "id",
            selectvalue: "value"
          },
          {
            type: "v_singleSelect",
            title: "文件来源",
            key: "Platform",
            data: [
              {
                id: '1',
                value: 'DTS',
              },
              {
                id: '2',
                value: 'OEM',
              }
            ],
            selectkey: "id",
            selectvalue: "value"
          },
          {
            type: "v_text",
            title: "文件名",
            key: "FileName"
          },
          {
            type: "v_datetime",
            title: "上传开始日期",
            key: "UpdateTimeStartDate"
            //defdate: moment(new Date()).add(-1, 'months').format("YYYY-MM-DD")
          },
          {
            type: "v_datetime",
            title: "上传结束日期",
            key: "UpdateTimeEndDate"
          }
        ]
      },
      buttonOptions: [
        {
          title: "下载",
          enable: true,
          method: function(e, item) {
            console.log("下载被点击");
            debugger;
            let seleCheckBoxs =
              _.$children[0].$refs.table.tableObj.bootstrapTable(
                "getSelections"
              ) || [];
            if (seleCheckBoxs.length) {
              let FileIDStr = "";
              seleCheckBoxs.map(item => {
                FileIDStr += item.FileId + ",";
              });

              FileManagerApi.GetUploadUrl({
                fileUploadIdList: FileIDStr.substring(0, FileIDStr.length - 1)
              }).then(res => {
                console.log(res);
                location.href = res.result.UploadUrl;
              });
            } else {
              alert("没有选择项");
            }
          }
        }
      ],
      tableOption: {
        tableId: "fileupload",
        tableName: "文件上传管理",
        url: config.pgyerApiUrl + FileManagerApi.GetFileUploadList.options.url,
        method: FileManagerApi.GetFileUploadList.options.method,
        uniqueId: TableUniqueId,
        clickToSelect: true,
        columns: [
          {
            align: "center",
            checkbox: true, // 显示复选框
            formatter: function(i, row) {}
          },
          {
            field: "FileName",
            title: "文件名",
            align: "center"
          },
          {
            field: "PlaceId",
            title: "上传点编号",
            align: "center"
          },
          {
            field: "EquipmentNumber",
            title: "枪号",
            align: "center",
          },
          {
            field: "FileSource",
            title: "文件类型（生产、物流）",
            align: "center",
            formatter: function(val, row) {
              return FileSource[row.FileSource];
            }
          },
          {
            field: "Platform",
            title: "文件来源（OEM、DTS）",
            align: "center",
            formatter: function(val, row) {
              return Platform[row.Platform];
            }
          },
          {
            field: "UpdateTime",
            title: "上传日期",
            align: "center",
            formatter: function(val, row) {
              return moment(val).format("YYYY-MM-DD HH:mm");
            }
          }
        ]
      }
    };
  },
  methods: {
    rowClick: function(rowItem, element, baseObject) {
      console.log(element);
      debugger;
    }
  },
  components: { vBasePage }
};
