import vTable from "@c/tables/table";
import vSearch from "@c/dynamicSearch/dynamic_Search";
import AreaApi from "../../DictModule/webapi/AreaApi";
import DataApi from "../../DictModule/webapi/DataApi";
import MatlApi from "../../MatlModule/webapi/MaterialsApi";
import ReportApi from "../webapi/ReportApi";
import moment from "moment";
import config from "@/config/index";
import { pageTool } from "@/js/util.js";
export default {
  data() {
    var _ = this;
    return {
      isEdit: false,
      search_controls: [
        {
          type: "v_text",
          title: "调拨单号1",
          key: "receiptno"
        },
        {
          type: "v_singleSelect",
          title: "选择产品",
          key: "materialsid",
          apiUrl: MatlApi.GetMaterialsList,
          //params: { aaa: "fff" },
          selectkey: "productId",
          selectvalue: "productName"
        },
        {
          type: "v_text",
          title: "批次号",
          key: "batchno"
        },
        {
          type: "v_datetime",
          title: "调拨开始日期",
          key: "allocationstartdate"
        },
        {
          type: "v_datetime",
          title: "调拨结束日期",
          key: "allocationenddate"
        },
        {
          type: "br",
          key: "bra"
        },
        {
          type: "v_doubleSelect",
          key: "allocationprovincecity",
          list: [
            {
              type: "v_singleSelect",
              title: "调拨省份",
              key: "allocationprovince",
              apiUrl: AreaApi.GetAllProvinceList,
              selectkey: "provinceid",
              selectvalue: "provincename",
              childrenkey: "allocationcity"
            },
            {
              type: "v_singleSelect",
              title: "调拨城市",
              key: "allocationcity",
              apiUrl: AreaApi.GetCityOfProvinceList,
              parendPostKey: "AreaId",
              selectkey: "cityid",
              selectvalue: "cityname"
            }
          ]
        },
        {
          type: "v_doubleSelect",
          key: "allocationlevelunit",
          list: [
            {
              type: "v_singleSelect",
              title: "调拨层级",
              key: "allocationlevel",
              apiUrl: DataApi.GetSendUnitLevelList,
              selectkey: "dataid",
              selectvalue: "dataname",
              childrenkey: "allocationunit"
            },
            {
              type: "v_singleSelect",
              title: "调拨单位",
              key: "allocationunit",
              apiUrl: DataApi.GetSendAllocationList,
              parendPostKey: "CatagoryId",
              selectkey: "id",
              selectvalue: "name"
            }
          ]
        },
        {
          type: "br",
          key: "brb"
        },
        {
          type: "v_doubleSelect",
          key: "receivprovincecity",
          list: [
            {
              type: "v_singleSelect",
              title: "收货省份",
              key: "receivprovince",
              apiUrl: AreaApi.GetAllProvinceList,
              selectkey: "provinceid",
              selectvalue: "provincename",
              childrenkey: "receivcity"
            },
            {
              type: "v_singleSelect",
              title: "收货城市",
              key: "receivcity",
              apiUrl: AreaApi.GetCityOfProvinceList,
              parendPostKey: "AreaId",
              selectkey: "cityid",
              selectvalue: "cityname"
            }
          ]
        },
        {
          type: "v_doubleSelect",
          key: "receivlevelunit",
          list: [
            {
              type: "v_singleSelect",
              title: "收货层级",
              key: "receivlevel",
              apiUrl: DataApi.GetReceivUnitLevelList,
              selectkey: "dataid",
              selectvalue: "dataname",
              childrenkey: "receivunit"
            },
            {
              type: "v_singleSelect",
              title: "收货单位",
              key: "receivunit",
              apiUrl: DataApi.GetReceivAllocationList,
              parendPostKey: "CatagoryId",
              selectkey: "id",
              selectvalue: "name"
            }
          ]
        },
        {
          type: "br",
          key: "brc"
        },
        {
          type: "v_text",
          title: "产品数码",
          key: "productcode"
        },
        {
          type: "v_datetime",
          title: "生产开始时间",
          key: "makingstartdate",
          format: "yyyy-mm-dd hh:ii",
          minView: "0",
          momentformat: "YYYY-MM-DD HH:mm"
        },
        {
          type: "v_datetime",
          title: "生产结束时间",
          key: "makingenddate",
          format: "yyyy-mm-dd hh:ii",
          minView: "0",
          momentformat: "YYYY-MM-DD HH:mm"
        }
      ],
      searchOptions: {
        requiredCondition: true
      },
      tableOption: {
        tableId: "allodetailreport",
        tableName: "调拨明细报表",
        //url: config.pgyerApiUrl + ReportApi.AllocationDetailReportPage.options.url,
        method: ReportApi.AllocationDetailReportPage.options.method,
        columns: [
          { field: "receipT_NO", title: "调拨单号", align: "center" },
          { field: "materialS_SN", title: "产品编码", align: "center" },
          { field: "materialS_NAME", title: "产品名称", 
            align: "left",
            width:"200px",
            formatter: pageTool.formartSubStr
          },
          { field: "materialS_STANDARD", title: "产品规格", align: "center" },
          { field: "batchNo", title: "批次号", align: "center" },
          { field: "sendProvince", title: "调拨省", align: "center" },
          { field: "sendCity", title: "调拨城市", align: "center" },
          { field: "receipT_SENDER", title: "调拨单位", align: "center" },
          { field: "receivProvince", title: "收货省", align: "center" },
          { field: "receivCity", title: "收货城市", align: "center" },
          { field: "receipT_RECEIVER", title: "收货单位", align: "center" },
          { field: "materialS_DIGITNO", title: "产品数码", align: "center" },
          { field: "amount", title: "数量", align: "center" },
          { field: "deviceno", title: "扫描枪编号", align: "center" },
          {
            field: "makingDate",
            title: "生产时间",
            align: "center",
            formatter: function(val, row) {
              return moment(val).format("YYYY-MM-DD HH:mm:ss");
            }
          },
          {
            field: "receipT_DATE",
            title: "调拨日期",
            align: "center",
            formatter: function(val, row) {
              return moment(val).format("YYYY-MM-DD HH:mm:ss");
            }
          }
        ]
      }
    };
  },
  computed: {
    exportOptions: function() {
      return {
        id: this.tableOption.tableId,
        name: this.tableOption.tableName,
        url: ReportApi.AllocationDetailReportExport
      };
    }
  },
  watch: {
    isEdit: {
      handler: function(curVal, oldVal) {
        if (curVal) {
          var _ = this;
          setTimeout(function() {
            _.initValidate();
          }, 100);
        }
      }
    }
  },
  mounted: function() {
    var _ = this;
    _.$refs.table.tableObj.on("click", "a", function() {
      //_.editModel();
    });
  },
  methods: {
    uploadCallback: function(a, b) {
      console.log(a);
      console.log(b);
    },
    search: function(params) {
      this.$refs.table.tableOption.url =
        config.pgyerApiUrl + ReportApi.AllocationDetailReportPage.options.url;
      this.$refs.table.tableOption.params = params;
      this.$refs.table.operation("refresh");
    },
    initValidate: function() {
      this.validator = $(this.$refs.formBody).bootstrapValidator({
        fields: {
          No: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "编号只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "编号不可为空！"
              }
            }
          },
          Name: {
            validators: {
              regexp: {
                regexp: /^([\u4e00-\u9fa5-a-zA-Z0-9-_.]{1,50})*$/,
                message: "名称只能为中文、英文字母、数字！"
              },
              notEmpty: {
                message: "名称不可为空！"
              }
            }
          }
        }
      });
    }
  },
  components: { vTable, vSearch }
};
